CREATE TABLE athletes (id BIGINT AUTO_INCREMENT, first_name VARCHAR(255) NOT NULL, second_name VARCHAR(255) NOT NULL, rank VARCHAR(255) NOT NULL, biography LONGBLOB NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE basic_exercises (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL UNIQUE, type VARCHAR(255) NOT NULL, description LONGBLOB NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE categories (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL UNIQUE, PRIMARY KEY(id)) ENGINE = INNODB;
